mapper.select:
select user_code, user_id, user_pw, user_name, user_phone, user_address, user_eatlist, user_allergy, user_likelist
from user
where user_id=#{user_id}

select user_code
from user
where user_code=#{user_code}

select user_id, user_name, user_eatlist
from user
where user_code = #{user_code}

mapper.update:
update 	user
set 	user_pw = "0000"
where 	user_id = #{user_id}

update 	user
set 	user_address = #{user_address},
		user_phone = #{user_phone},
		user_pw = #{user_pw}
where 	user_id = #{user_id}

update user
set user_likelist = #{user_likelist}
where user_code = #{user_Code}
